var ColorPicker = window.VueColorPicker;

var app = new Vue({
    el: '#app',
    components: {
        ColorPicker: ColorPicker
    },
    data: {
        color: {
            hue: 50,
            saturation: 100,
            luminosity: 50,
            alpha: 1
        }
    },
    methods: {
        onInput: function(hue) {
            this.color.hue = hue;
        }
    }
});